example.programlisting:
var $thing = $('#naoexistente');

var cb = function() {
    console.log('pronto!');
};

if ($thing.length) {
    $thing.fadeIn(300, cb);
} else {
    cb();
}

$('div.funtimes').animate(
    {
        left : "+=50",
        opacity : 0.25
    }, 
    300, // duração
    function() { console.log('pronto!'); // callback
});
